mapper.update:
        UPDATE workspace
        SET wrkspc_name = #{wrkspcName}
	        ,description = #{description}
	        ,wrkspc_type = #{wrkspcType}
	        ,admin_user_id = #{adminUserId}
	        ,update_date = NOW()
	        ,update_user_id = #{updateUserId}
        WHERE wkrspc_id = #{wkrspcId}

mapper.select:
SELECT wkrspc_id, wrkspc_name, description, wrkspc_type, admin_user_id, update_date, update_user_id
FROM workspace
WHERE wkrspc_id = #{wkrspcId}
